import React, { Component } from 'react';
import axios from 'axios'
import {BrowserRouter, Switch, Route} from 'react-router-dom'
import Home from './components/Home'
import Login from './components/registrations/Login'
import Signup from './components/registrations/Signup'
import './App.css';
import AddProperty from './components/forms/AddProperty';


export default class App extends Component {
constructor(props){
  super(props);
  this.state = {
    landlordLogin: true,
    isLoggedIn: false,
    landlord: {},
    properties: {},
    tenants:{},
    issues:{},
    todos:{}

  };
}

componentDidMount(){
  this.loginStatus()
}

loginStatus = () => {
  axios.get('http://localhost:3001/logged_in', {withCredentials: true})
  .then(response => {
    if (response.data.logged_in){
      this.handleLogin(response.data)
    }else{
      this.handleLogout()
    }
  })
  .catch(error => console.log('api errors:', error))
}


handleLogin = (data) => {
const {landlord, properties, tenants, issues, todos} = data
debugger
this.setState({
  isLoggedIn: true,
    landlord: {},
    properties: {},
    tenants:{},
    issues:{},
    todos:{}
})
}

handleLogout = () => {
  this.setState({
    isLoggedIn: false,
    landlord: {},
    properties: {},
    tenants:{},
    issues:{},
    todos:{}
  })
}

setValueAccess = (value) => {
  value == 'landlord' ? this.setState({landlordLogin:true}) : this.setState({landlordLogin:false})
  console.log(value)
}



render() {
  return (
    <div>
      <BrowserRouter>
        <Switch>
          <Route 
            exact path='/' 
            render={props => (
            <Home 
              user={this.state.landlord}
              setValueAccess={this.setValueAccess} 
              {...props} 
              handleLogout={this.handleLogout} 
              setValueAccess={this.setValueAccess}
              loggedInStatus={this.state.isLoggedIn}
              handleLogin={this.handleLogin} 
            />
            )}
          />
          <Route 
            exact path='/login' 
            render={props => (
            <Login {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn}/>
            )}
          />
          <Route 
            exact path='/signup' 
            render={props => (
            <Signup {...props} setValueAccess={this.setValueAccess } handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn}/>
            )}
          />
        </Switch>
      </BrowserRouter>
    </div>
  );
}
}
