import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';


const IssueList = ({issues}) => {

  const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
    },
  }));
  const classes = useStyles();
  const [checked, setChecked] = React.useState([false]);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };


  const renderIssues = issues.map(issue =>

    <Link key={issue.id} to={`/issues/${issue.id}`}  > <h1>{issue.message}</h1> </Link>
    )



    return (
      <List dense className={classes.root}>
        {issues.map((issue) => {
          const labelId = `checkbox-list-secondary-label-${issue.message}`;
          return (
            <ListItem key={issue.id} button>
              <ListItemAvatar>
                <Avatar
                  // alt={`Avatar n°${issues + 1}`}
                  // src={`/static/images/avatar/${value + 1}.jpg`}
                />
              </ListItemAvatar>
              <ListItemText id={labelId} primary={` ${issue.message}`} />
              <ListItemSecondaryAction>
                <Checkbox
                  edge="end"
                  onChange={handleToggle(issue)}
                  checked={checked.indexOf(issue) !== -1}
                  inputProps={{ 'aria-labelledby': labelId }}
                />
                   <Checkbox
                  edge="end"
                  onChange={handleToggle(issue)}
                  checked={checked.indexOf(issue) !== -1}
                  inputProps={{ 'aria-labelledby': labelId }}
                />
              </ListItemSecondaryAction>
            </ListItem>
          );
        })}
      </List>
    );
      }


export default IssueList
