import React, {useEffect, useState} from 'react'
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
  });

export default function PropertyDetails({property, showAllProperties}) {
    const styles = useStyles();

    //load the property details
    const [details, setDetails] = useState(0);
    const [tenants, setTenants] = useState(0);
    const [issues, setIssues] = useState(0);
    const [todos, setTodos] = useState(0);

    useEffect(() => {
        const url = `http://localhost:3001/properties/${property}`
        fetch(url)
          .then(res => res.json())
          .then(data => {
            setDetails(data.property);
            setTenants(data.tenants);
            setTodos(data.todos);
            setIssues(data.issues);
          });
    }, [])

    // render the details after is loaded
    const renderDetails = () =>{
        return(
            <React.Fragment>
                <Typography variant="h2" component="h2" gutterBottom>
                    {details.address}
                    {generateTable(tenants)}
                </Typography>
            </React.Fragment>
        )
    }

    //generate table for each state (tenants, issues, and todos)
    const generateTable = (data) =>{
        let newTenants = data.map(obj => ({
            "Full Name": obj.first_name + " " + obj.last_name,
            dob : obj.dob,
            email: obj.email
          }))

        let keys = Object.keys(newTenants[0]);
        let values = Object.values(newTenants[0])
        console.log(values, keys);

        return (
            <TableContainer component={Paper}>
                <Table className={styles.table} aria-label="simple table">
        <TableHead>
          <TableRow>

            {keys.map(k => (<TableCell key={k}>{k}</TableCell>) )}
          </TableRow>
        </TableHead>
        <TableBody>
        {newTenants.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row["Full Name"]}
              </TableCell>
              <TableCell align="left">{row.dob}</TableCell>
              <TableCell align="left">{row.email}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
            </TableContainer>
        )
    }

    //main return
    return (
        <Container maxWidth="xl">
            <div>{issues !== 0 ? renderDetails() : null}</div>
            <Button onClick={showAllProperties} variant="contained" color="primary">Go back</Button>
        </Container>
    )
}
